import { Box, Grid, Pagination, Stack, Typography } from "@mui/material";
import React, { useEffect, useState } from "react";
import PostsCard from "./PostsCard";
import Tepsikebab from "../../static/tepsikebab.jpg";
//import axios from 'axios'
const Posts = () => {
  const [blog, setBlog] = useState([])

  useEffect( () => {
    const fetchData = async () => {
      try {
        //const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/blogs/`);
        const res = [
          {
              "id": 1,
              "title": "Tepsi Kebab",
              "slug": "123",
              "excerpt": "asd",
              "content": "asdasdas",
              "contentTwo": "asdasd",
              "image": "",
              "ingredients": "asdasd",
              "postlabel": "POPULAR",
              "category": 1
          }
      ]
        setBlog(res)
      } catch (error) {
        console.log(error)
      }
    }
    fetchData()
  }, [])


  const [post, setPost] = useState([])

  useEffect( () => {
    const fetchData = async () => {
      try {
        //const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/PopularPostsApiView/`)
        const res = [
          {
              "id": 1,
              "title": "123",
              "slug": "123",
              "excerpt": "asd",
              "content": "asdasdas",
              "contentTwo": "asdasd",
              "image": "",
              "ingredients": "asdasd",
              "postlabel": "POPULAR",
              "category": 1
          }
      ]
        setPost(res)
      } catch (error) {
        console.log(error)
      }
    }
    fetchData()
  }, [])


  return (
    <Box>
      <Typography variant="h4" align="center">
        {" "}
        Latest Recipes{" "}
      </Typography>
      <Grid
        container
        columnSpacing={{ xs: 0, sm: 1, md: 1 }}
        direction={"column"}
      >
          <Grid item xs>
          <PostsCard title="Tepsi Kebab" excerpt="Tray kebab, also known as lahm-Ä± sini, is just one of the delicious dishes from Antakya. Although it looks like a giant vegetable meatball on the tray, it is actually a special kebab in every way. Meat and vegetables are carefully selected, all these ingredients are cut into small pieces with a shredder and a wonderful taste emerges. The important thing at this point is not to water any vegetables and to give all the water to the kebab as flavor while cooking.

The quality of the meat and the single grinding of the minced meat are the factors that make the kebab taste much more delicious. If we cannot go to Antakya and eat tray kebab on site, should we be deprived of this taste? Of course we shouldn't stay." image={Tepsikebab} blogHref="/details/1" myDirection="flex" />
        </Grid>
      </Grid>
      <Typography
        variant="h4"
        bgcolor={"black"}
        color="white"
        align="center"
        mt={4}
        mb={4}
      >
        Most Popular
      </Typography>
      <Grid container columnSpacing={{ xs: 0, sm: 1, md: 1 }} direction={{md:'row'}}>
      {post.map(popular => (
          <Grid item md={6} xs={12} sm={6}>
          <PostsCard title={popular.title} excerpt={popular.excerpt} image={`${process.env.REACT_APP_API_URL}${popular.image}`} myDirection={"block"} />
        </Grid>
        ))
      }
      </Grid>
      <Stack
        spacing={2}
        mt={3}
        mb={3}
        justifyContent="center"
        alignItems={"center"}
      >
        <Pagination count={10} color={"warning"} />
      </Stack>
    </Box>
  );
};

export default Posts;
